  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'Primary Key',
  `app_id` int(10) unsigned NOT NULL COMMENT ' app foreign key',
  `sender_id` int(10) unsigned NOT NULL COMMENT 'sender(normal user) foreign key',
  `receiver_id` int(10) unsigned NOT NULL COMMENT 'receiver(typically user) foreign key',
  `dicussion` longtext COLLATE utf8mb4_unicode_ci,
  `status` enum('1','2','3') COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '1' COMMENT '1=>open, 2=>close, 3=>deleted',
  `created_at` timestamp NULL DEFAULT NULL,
  `updated_at` timestamp NULL DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `app_general_dicussions_app_id_foreign` (`app_id`),
  KEY `app_general_dicussions_sender_id_foreign` (`sender_id`),
  KEY `app_general_dicussions_receiver_id_foreign` (`receiver_id`),
  CONSTRAINT `app_general_dicussions_app_id_foreign` FOREIGN KEY (`app_id`) REFERENCES `app_basic` (`id`),
  CONSTRAINT `app_general_dicussions_receiver_id_foreign` FOREIGN KEY (`receiver_id`) REFERENCES `users` (`id`),
  CONSTRAINT `app_general_dicussions_sender_id_foreign` FOREIGN KEY (`sender_id`) REFERENCES `users` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;

